import React from "react";
import { useEffect } from "react";

import { getMinutes, getSeconds, getMilliseconds } from "date-fns";

export default function StopWatch({ interval, isStopped }) {
  // const [secondsStartDegree, setSecondsStartDegree] = useState(0);
  // const [minutesStartDegree, setMinutesStartDegree] = useState(0);
  //
  // const [seconds, setSeconds] = useState(getSeconds(interval));
  // const [minutes, setMinutes] = useState(getMinutes(interval));
  // // let seconds = duration.seconds();
  // // let minutes = duration.minutes();
  // const [milliseconds, setMilliseconds] = useState(
  //     getMilliseconds(interval) / 10
  // );

  ///UPDATED!!!!!!
  // const [duration, setDuration] = useState(interval);
  // // const [duration, setDuration] = useState(moment.duration(interval));
  // useEffect(() => {
  //   setSeconds(getSeconds(interval));
  //   setMinutes(getMinutes(interval));
  //   setMilliseconds(getMilliseconds(interval) / 10);
  //   // setDuration(moment.duration(interval));
  // }, [interval]);

  // const [duration, setDuration] = useState(getTime(interval));
  // // const [duration, setDuration] = useState(moment.duration(interval));
  // useEffect(() => {
  //   setDuration(getTime(interval));
  //   // setDuration(moment.duration(interval));
  // }, [interval]);

  // let milliseconds = Math.round(duration.milliseconds() / 10);

  useEffect(() => {
    if (isStopped === true) {
      const elem = document.getElementById("style");
      if (elem) {
        elem.remove();
      }
      const stop_style = document.createElement("style");
      stop_style.setAttribute("id", "style");
      // setMilliseconds(Math.round(getMilliseconds(interval)));
      // setSeconds(getSeconds(interval));
      // setMinutes(getMinutes(interval));
      console.log("stopped==true");
      console.log("interval");
      console.log(interval);
      console.log("milliseconds");
      console.log(Math.round(getMilliseconds(interval)));
      console.log("seconds");
      console.log(getSeconds(interval));
      console.log("minutes");
      console.log(getMinutes(interval));
      const secondsStartDegree =
        (360 / 60) * getSeconds(interval) +
        0.07 * Math.round(getMilliseconds(interval) / 10);
      const minutesStartDegree =
        (360 / 60) * getMinutes(interval) + (6 / 60) * getSeconds(interval);
      // setSecondsStartDegree((360 / 60) * getSeconds(interval) + 0.07 * (Math.round(getMilliseconds(interval))));
      // setMinutesStartDegree((360 / 60) * getMinutes(interval) + (6 / 60) * getSeconds(interval));
      console.log("secondsStartDegree");
      console.log(secondsStartDegree);
      console.log("minutesStartDegree");
      console.log(minutesStartDegree);
      stop_style.innerHTML =
        ".clock__hand--minute { transform: rotate(" +
        minutesStartDegree +
        "deg);}" +
        ".clock__hand--second { transform: rotate(" +
        secondsStartDegree +
        "deg);}";
      document.getElementsByTagName("head")[0].appendChild(stop_style);
    }
    if (isStopped === false) {
      const elem = document.getElementById("style");
      if (elem) {
        elem.remove();
      }
      const style = document.createElement("style");
      style.setAttribute("id", "style");
      // setMilliseconds(Math.round(getMilliseconds(interval)));
      // setSeconds(getSeconds(interval));
      // setMinutes(getMinutes(interval));
      console.log("stopped==false");
      console.log("interval");
      console.log(interval);
      console.log("milliseconds");
      console.log(Math.round(getMilliseconds(interval)));
      console.log("seconds");
      console.log(getSeconds(interval));
      console.log("minutes");
      console.log(getMinutes(interval));
      const secondsStartDegree =
        (360 / 60) * getSeconds(interval) +
        0.07 * Math.round(getMilliseconds(interval) / 10);
      const minutesStartDegree =
        (360 / 60) * getMinutes(interval) + (6 / 60) * getSeconds(interval);
      console.log("secondsStartDegree");
      console.log(secondsStartDegree);
      console.log("minutesStartDegree");
      console.log(minutesStartDegree);
      style.innerHTML =
        "@keyframes clock-hand-rotate--minute { from {transform: rotate(" +
        minutesStartDegree +
        "deg)}" +
        "to {transform: rotate(" +
        (minutesStartDegree + 360) +
        "deg)}}" +
        "@keyframes clock-hand-rotate--second {from {transform: rotate(" +
        secondsStartDegree +
        "deg)}" +
        "to {transform: rotate(" +
        (secondsStartDegree + 360) +
        "deg)}}" +
        ".clock__hand--minute { animation: clock-hand-rotate--minute 3600s linear infinite;}" +
        ".clock__hand--second {animation: clock-hand-rotate--second 59s linear infinite;}";
      document.getElementsByTagName("head")[0].appendChild(style);
    }
    return () => {
      let elem = document.getElementById("style");
      if (elem) {
        elem.remove();
      }
    };
  }, [isStopped]);
  // }, [isStopped]);

  return (
    <div>
