import React from "react";
// import { useState, useEffect } from "react";
import {getMilliseconds, getMinutes, getSeconds} from 'date-fns';

export default function Timer({ interval }) {

  // const duration = getTime(interval);

  // const [duration, setDuration] = useState(format(interval,'mm:ss.SS'));
  // useEffect(() => setDuration(format(interval,'mm:ss.SS')), [interval]);

  // const [duration, setDuration] = useState(moment.duration(interval));
  // useEffect(() => setDuration(moment.duration(interval)), [interval]);


  //Latest stupid attempt
  // const minutes = duration > 60000 ?  duration / 60000 : 0;
  // const seconds = duration > 1000 ? duration / 1000 : 0;
  // const milliseconds = Math.floor((duration - minutes*60000 - seconds*1000) / 1000);

  const seconds = getSeconds(interval);
  const minutes = getMinutes(interval);
  // let seconds = duration.seconds();
  // let minutes = duration.minutes();
  const milliseconds = Math.round (getMilliseconds(interval));

   const pad = n => (n < 10 ? "0" + n : n);
  // //const milliseconds = pad()
   //const milliseconds = Math.round(duration.milliseconds() / 10);
  return (
    <div>
      {pad(minutes)}:{pad(seconds)}.{pad(milliseconds)}
    </div>
  );
  /*{pad(duration.minutes())}:{pad(duration.seconds())}.{pad(milliseconds)}*/ //was in return
}
