import React from "react";
import LapsesList from "./LapsesList.js";
import StopWatch from "./StopWatch.js";
import Timer from "./Timer.js";
import { useState, useEffect } from "react";

import { addMilliseconds,subMilliseconds, getTime, Interval, getMilliseconds,differenceInMilliseconds,
  differenceInSeconds, format, toDate } from 'date-fns';

export default function App() {
  const [lapses, setLapses] = useState([]);
  const [now, setNow] = useState(0);
  const [start, setStart] = useState(0);
  // const[interval,setInterval] = useState(differenceInMilliseconds(now,start));
  const [myInterval, setMyInterval] = useState(null);
  const [duration, setDuration] = useState(0);
  //UPDATED PART
  const [intervalMilliseconds, setintervalMilliseconds] = useState(0);
  const [intervalSeconds, setintervalSeconds] = useState(0);
  const [intervalMinutes, setintervalMinutes] = useState(0);
//
  const [StartB, setStartB] = useState("START");
  const [LapB, setLapB] = useState("LAP");
  const [ResetB, setResetB] = useState("RESET");
  const [StopB, setStopB] = useState("STOP");
  const [ResumeB, setResumeB] = useState("RESUME");
  const [LANG, setLANG] = useState("eng");

  const [isStopped, setStop] = useState(0);
  useEffect(() => {
    if (isStopped) {
      clearInterval(myInterval);
    } else if (start > 0) {
      const id = setInterval(() => {
      //   setNow(new Date().getTime());
        //       // }, 73);

        setNow(getMilliseconds(new Date()))
      }, 73);
      setMyInterval(id);
    }
    return () => clearInterval(myInterval);
  }, [isStopped]);

  // const timer = () => {
  //   setInterval(() => {
  //     setNow(new Date().getTime());
  //   }, 73);
  // };

  const addLapse = () => {
    const timestamp = new Date();
    lapses.length > 0
    ? (setDuration(differenceInMilliseconds(timestamp, start) - (lapses[0]["end_time"])))
    : (setDuration(differenceInMilliseconds(timestamp , start)));
    setLapses([{ end_time: differenceInMilliseconds(timestamp - start), duration: duration }, ...lapses]);
    setStop(false);

    // const timestamp = new Date().getTime();
    // let duration;
    // lapses.length > 0
    //   ? (duration = timestamp - (lapses[0]["end_time"] + start))
    //   : (duration = timestamp - start);
    // setLapses([{ end_time: timestamp - start, duration: duration }, ...lapses]);
    // setStop(false);
  };

  const handleStart = () => {
    setStart(new Date());
    setNow(new Date());
    setStop(false);

    // setStart(new Date().getTime());
    // setNow(new Date().getTime());
    // setStop(false);
  };

  const handleResume = () => {
    setStart(subMilliseconds(new Date(), interval));
    setNow(new Date());
    // setStart(new Date().getTime() - interval);
    // setNow(new Date().getTime());
    setStop(false);
  };

  const handleReset = () => {
    setNow(0);
    setStart(0);
    setStop(0);
    setLapses([]);
  };

  const handleStop = () => {
    setStop(true);
  };

  const ENG = () => {
    if (LANG !== "eng") {
      setStartB("START");
      setLapB("LAP");
      setResetB("RESET");
      setStopB("STOP");
      setResumeB("RESUME");
      setLANG("eng");
    }
  };
  const RU = () => {
    if (LANG !== "ru") {
      setStartB("СТАРТ");
      setLapB("КРУГ");
      setResetB("СБРОС");
      setStopB("СТОП");
      setResumeB("ПРОДОЛЖИТЬ");
      setLANG("ru");
    }
  };

  return (
    <div className="App">
      <div className="lang_switcher">
        <button onClick={() => RU()}>РУС</button>
        <button onClick={() => ENG()}>ENG</button>
      </div>
      <div className="Timer">
        <Timer interval={interval} />
      </div>
      <div className="lapseTimer">
        {lapses.length > 0 ? (
          <Timer interval={interval - lapses[0]["end_time"]} />
        ) : (
          <Timer interval={interval} />
        )}
      </div>
      <div className="ClockContainer">
        <StopWatch isStopped={isStopped} interval={interval} />
      </div>
      {start > 0 && !isStopped ? (
        <div className="Buttons twoButtons">
          <button onClick={() => addLapse()} className="lapButton">
            {LapB}
          </button>
          <button onClick={() => handleStop()} className="stopButton">
            {StopB}
          </button>
        </div>
      ) : isStopped ? (
        <div className="Buttons twoButtons">
          <button onClick={() => handleResume()} className="startButton">
            {ResumeB}
          </button>
          <button onClick={() => handleReset()} className="resetButton">
            {ResetB}
          </button>
        </div>
      ) : (
        <div className="Buttons twoButtons">
          <button onClick={() => handleStart()} className="startButton">
            {StartB}
          </button>
          <button onClick={() => handleReset()} className="resetButton">
            {ResetB}
          </button>
        </div>
      )}
      <LapsesList lapses={lapses} />
    </div>
  );
}
